import React from "react";
import {
  Nav,
  NavbarContainer,
  NavLogoItem,
  NavLogoContainer,
  NavSlide,
  NavItem,
  NavLinks,
} from "./NavbarElements";




const Navbar = ({toggle, isVisible, swiper}) => {

  function pSwipe (swiper) {
    const mySwiper = document.querySelector('.swiper-container').swiper
    mySwiper.update();
    for (let i = 0; i < 7; i += 1) {
      mySwiper.removeSlides(i);
    }
  }
  return (
    <>
      <Nav>
        <NavbarContainer>
          <NavLogoContainer>

            <NavLogoItem isVisible={isVisible} onClick={toggle}>Miguel Lopes</NavLogoItem>
            <NavLogoItem id="portfolioLink">Designer</NavLogoItem>
          </NavLogoContainer>

          <NavSlide>

            <NavItem>
              <NavLinks id="productsLink" onClick={pSwipe(swiper)}>Product/</NavLinks>
            </NavItem>
            <NavItem>
              <NavLinks id="graphicsLink">Graphic</NavLinks>
            </NavItem> 

          </NavSlide>
        </NavbarContainer>
      </Nav>
    </>
  );
};

export default Navbar;
