// import Swiper core and required components
import SwiperCore, { Navigation, Autoplay, Parallax } from "swiper";

import { Swiper, SwiperSlide } from "swiper/react";

import { ImgWrap, Img } from "./SwiperElements";

// Import Swiper styles
import "swiper/swiper.scss";
import "swiper/components/navigation/navigation.scss";

import {Images} from "../../assets/images/";

// install Swiper components
SwiperCore.use([Navigation, Autoplay, Parallax]);

export const FullSwiper = ({ id, img, alt }) => {
  const slideImages =[];
    for (let i=0; i<29 ;i+=1){
      //let fotoNumber = 69+i;
        slideImages[i] = {
            id: "slide"+(i+1),
            img: Image[0],
            alt: "slide"+(i+1)
        }
    };


  const slides = [];
  for (let i = 0; i < 29; i += 1) {
    slides.push(
      <SwiperSlide key={slideImages[i].id}>
        <ImgWrap>
          <Img src={slideImages[i].img} alt={slideImages[i].alt} />
        </ImgWrap>
      </SwiperSlide>
    );
  }

  return (
    <Swiper
      slidesPerView={1}
      loop={true}
      navigation={{
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
      }}
      autoplay={{ delay: "2000" }}
      onSlideChange={() => console.log("slide change")}
      onSwiper={(swiper) => console.log(swiper)}
    >
      {slides}
    </Swiper>
  );
};
