import React from "react";
import { HeroContainer, HeroCarousel, HeroAbout, AboutLink } from "./HeroElements";
const HeroSection = () => {
  return (
    <HeroContainer>
      <HeroCarousel id="carousel"></HeroCarousel>
      <HeroAbout id="about">
        <p>Graduated in ESAD.cr currently living in Lisbon, Portugal</p>
        <p>
          His work ranges from installation/urban requalification to graphic and
          product design. Miguel also participated in several exhibitions
          including experimentadesign 2013, ParisDesignWeek with MeetMyProject,
          he collaborated with architecture studio Paratelier with projects
          connected between spatial and product design. His work appeared on
          reputable on and offline magazines like ICON, Domus, Minimalissimo,
          Defringe, Gessato, Thisispaper, Opumo, Gooood, Leibal and Lovark.
        </p>
        <AboutLink to="https://www.instagram.com/miguelmlopes">@miguellopes</AboutLink>
        <AboutLink tel="351-913-700-157">T:+351 913 700 157</AboutLink>
        <AboutLink mail="miguel@miguellopes.info">miguel@miguellopes.info</AboutLink>
      </HeroAbout>
    </HeroContainer>
  );
};

export default HeroSection;
