import styled from "styled-components";
import { Link as LinkR } from "react-router-dom";

export const AboutContainer = styled.div `
    margin-top: 120px;
    width:100%;
    display:grid;
    max-width: 720px;
    transition: width 2s linear 10s;
    ${'' /* opacity: ${({ isVisible}) => (isVisible ? '100%' : '0')};
    left: ${({ isVisible})=> (isVisible ? '0' : '-100%')};  */}
`
export const Description = styled.p`
  margin: 0;
`;

export const AboutLink = styled(LinkR)`
  margin-top: 25px;
  color: #fff;
  text-decoration: none;
  cursor: pointer;

  &.visited {
    color: #fff;
  }
  &.hover {
    color: #a6a6a6;
  }
`;
