import styled from "styled-components";

export const HeroContainer = styled.div`
  margin-top: 70px;
  grid-template-columns: 500px auto;
  align-items: center;
  display: grid;
`;

export const HeroBlank = styled.div`
  display: grid;
`;
export const HeroActive = styled.div`
  display: grid;
  margin-left: 55px;
`;
