import React, {useState} from "react";
import AboutSection from "../AboutSection";
import SwiperSection from "../Swiper";
import { HeroContainer, HeroBlank, HeroActive } from "./HeroElements";
const HeroSection = () => {

  const [isVisible, setIsInvisible] = useState(false)

  const visibleAbout = () => {
    display: isVisible ? "block" : "none"
  };

  return (
    <HeroContainer>
      <HeroBlank></HeroBlank>
      <HeroActive>
        <SwiperSection isVisible={!isVisible} style={visibleAbout}/>
        <AboutSection isVisible={isVisible} style={visibleAbout}/>
      </HeroActive>
    </HeroContainer>
  );
};
export default HeroSection;
